import React from 'react'
import { signInWithGoogle } from "./lib/firebase"
import { authContext } from "./AuthProvider"
import { useContext } from 'react'
import { Redirect } from "react-router-dom";
import './L'
export const Login = () => {
    let user = useContext(authContext);
    console.log(user);

    return (
        <>
            <div>
                {user ? <Redirect to="/" /> : ""}
                <img src="https://9to5google.com/wp-content/uploads/sites/4/2020/01/google-classroom-cover.jpg?quality=82&strip=all&w=1000" alt="" />

            </div>
            <div classname="login-option">
                <h5>Already have an account in google ?
                <button
                    onClick={() => {
                        signInWithGoogle()
                    }}
                    className="btn btn-primary m-4">Login with Google</button>
                    </h5>

            </div>
        </>
    )
}

export default Login
