import { AppBar, Toolbar, Typography } from '@material-ui/core'
import React from 'react'
import { useStyles } from './style'
import Add from '@material-ui/icons/Add';
import AppsIcon from '@material-ui/icons/Apps';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { CreateClass } from '../CreateClass/CreateClass';

export const Header = ({ children }) => {
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };
    const classes = useStyles()
    return (
        <div className={classes.root}>
            <AppBar className={classes.appBar} position="static">
                <Toolbar className={classes.toolbar}>
                    <div className={classes.headerWrapper}>
                        {children}
                        <img
                            src="https://res.cloudinary.com/demo/image/fetch/w_0.5,f_webp,q_70,b_white/https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png"
                            alt="Classroom"
                        />
                        <Typography variant="h6" className={classes.title}>
                            Classroom
                        </Typography>
                    </div>


                    <div className={classes.header__wrapper__right}>
                        <Add className={classes.icon} onClick={handleClick}/>
                        <Menu
                            id="simple-menu"
                            anchorEl={anchorEl}
                            keepMounted
                            open={Boolean(anchorEl)}
                            onClose={handleClose}
                        >
                            <MenuItem onClick={handleClose}>Join Class</MenuItem>
                            <MenuItem onClick={handleClose}>Create Class</MenuItem>
                            
                        </Menu>

                        <AppsIcon className={classes.icon} />
                        <div>
                            <AccountCircleIcon className={classes.icon} />
                        </div>
                    </div>
                    <CreateClass/>

                </Toolbar>
            </AppBar>

        </div>
    )
}
