import React, { useState } from 'react'
import { useEffect } from 'react';
import { firestore } from '../lib/firebase';
export const Posts = (props) => {
    const [posts, setposts] = useState([])
    console.log(props);
    useEffect(() => {
        if (props.postInfo) {
            let unsubscribe = firestore.collection('announcements')
                .doc('classes')
                .collection(props.postInfo.id)
                .onSnapshot((snap) => {
                    setposts(snap.docs.map((doc) => doc.data()))
                })
        }
    }, [props.postInfo])
    console.log(posts);
    return (
        <div>
            {posts.map((item) =>{ 
            
                
                {if(item.documentURL){return(
                    <>
                    <div class="card-box">
                        <h3>{item.documentName}</h3>
                        <a>document: {item.documentURL}</a>
                        <div>{item.text}</div>
                    </div>
                </>)
                }else{return(
                    <>
                    <div class="card-box">
                        <h5>{item.text}</h5>
                    </div>
                </>)
                }}
               
                
            })}
        </div>
    )
}
